package testcom.ineuron;

public class patternassignment02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		program to print :-
//		11111
//		22222
//		33333
//		44444
		
		int n=4;
		for (int i=1;i<=n;i++) {
			for (int j=0;j<=n;j++) {
				System.out.print(i);
			}
			System.out.println();
		
		}
		
	}

}
